import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Character } from '../../interfaces/character.interface';

@Component({
  selector: 'dbz-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent {

  @Input()
  public characterList: Character[] = [{
    name: 'Trunks',
    power: 10
  }]


  @Output()
  public onDelete: EventEmitter <string>= new EventEmitter();




  onDeleteCharacter(id?:string): void{
    if(!id) return;
    this.onDelete.emit(id);
  }
}
<h4>Listado</h4>
<ul class="list-group">
  <li *ngFor="let character of characterList;
  let i = index;
  let isFirst = first;
  let isLast = last;
  let isEven = even;
  let isOdd = odd;
  "

  class="list-group-item d-flex justify-content-between align-items-center"
  [ngClass]="{
    'list-group-item-dark': isLast,
    'list-group-item-primary': isEven
  }">

  <div>
    <span class="text-primary"> {{i+1}}. </span>

    <span>{{character.name}} - </span>

    <strong>Power: </strong>

    <span>{{character.power}}</span>
  </div>

  <button (click)="onDeleteCharacter(character.id)" class="btn btn-danger">X</button>

</li>
</ul>
