import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html'
})
export class AppComponent {
  public title: string = 'Hola mundo';
  public counter: number = 10;

  increaseBy(value:number): void{
    this.counter += value;
  }

  resetCounter(): void{
    this.counter=10;
  }
}
<h1>{{title}}</h1>
